const articleButton=document.querySelector(".article__button"),closeButton=document.querySelector("#popup-close-button"),overlay=document.querySelector(".overlay"),counterSpan=document.querySelector("#counter"),getItemFromLS=e=>{if(window.localStorage)return localStorage.getItem(e);throw Error("Localstorage is not supported")},setItemInLS=(e,o)=>{if(!window.localStorage)throw Error("Localstorage is not supported");localStorage.setItem(e,o)},showPopup=()=>{overlay.style.display="block";var e=getItemFromLS("popup-rendering-counter");e?(e=JSON.parse(e).counter,setItemInLS("popup-rendering-counter",JSON.stringify({counter:++e})),5==e&&console.log("Popup renders 5 times")):setItemInLS("popup-rendering-counter",JSON.stringify({counter:1}))},handleClickCloseButton=()=>{overlay.style.display="none"};articleButton.addEventListener("click",showPopup),closeButton.addEventListener("click",handleClickCloseButton);